import React, { useState } from "react";
import { GoogleMap, LoadScript, Marker, InfoWindow, DirectionsService, DirectionsRenderer } from "@react-google-maps/api";

// Load API key from .env
const API_KEY = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;

// Container style for the map
const containerStyle = {
  width: "100%",
  height: "100vh",
};

// Center the map around Nairobi
const center = {
  lat: -1.286389,
  lng: 36.817223,
};

// Your real predicted data
const trafficData = [
  {
    Actual: 80,
    Predicted: 86.7942656344,
    timestamp: 1742284800000,
    road_name: "Thika Road",
    location: { lat: -1.286352078, lon: 36.8227607206 },
  },
  {
    Actual: 35,
    Predicted: 56.4447525611,
    timestamp: 1742288400000,
    road_name: "Thika Road",
    location: { lat: -1.301295454, lon: 36.8240535191 },
  },
  {
    Actual: 67,
    Predicted: 61.0186717083,
    timestamp: 1742292000000,
    road_name: "Moi Avenue",
    location: { lat: -1.2849540289, lon: 36.8287996864 },
  },
];

// Function to format timestamps
const formatTimestamp = (timestamp) => {
  const date = new Date(timestamp);
  return date.toLocaleString();
};

// Function to determine the event type based on predicted traffic count
const getEventType = (predictedCount) => {
  if (predictedCount > 80) return "ðŸš— Congested";
  else if (predictedCount > 40) return "ðŸš™ Moderate";
  return "ðŸš¶â€â™‚ï¸ Clear";
};

function App() {
  const [selectedMarker, setSelectedMarker] = useState(null);
  const [directionsResponse, setDirectionsResponse] = useState(null);
  const [routeRequested, setRouteRequested] = useState(false);
  const [origin, setOrigin] = useState("");
  const [destination, setDestination] = useState("");
  const [highlightAlternative, setHighlightAlternative] = useState(false);

  const handleRouteRequest = () => {
    if (!origin || !destination) {
      alert("Please enter both origin and destination!");
      return;
    }

    // Convert the entered origin/destination into LatLng
    const originLatLng = getLatLngFromAddress(origin);
    const destinationLatLng = getLatLngFromAddress(destination);

    // Call the Directions API to get an optimized route
    const directionsService = new window.google.maps.DirectionsService();
    directionsService.route(
      {
        origin: originLatLng,
        destination: destinationLatLng,
        travelMode: window.google.maps.TravelMode.DRIVING,
        drivingOptions: {
          departureTime: new Date(),
          trafficModel: "best_guess",
        },
      },
      (result, status) => {
        if (status === "OK") {
          setDirectionsResponse(result);
          setRouteRequested(true);
        } else {
          console.error("Error fetching directions: ", status);
        }
      }
    );
  };

  // A simple method to simulate address-to-latLng conversion (this would need a geocoding service)
  const getLatLngFromAddress = (address) => {
    // You would normally use a geocoding API to convert an address into lat/lng
    // For now, this is just a placeholder
    if (address.toLowerCase() === "thika road") return { lat: -1.286352078, lng: 36.8227607206 };
    if (address.toLowerCase() === "moi avenue") return { lat: -1.2849540289, lng: 36.8287996864 };
    return { lat: -1.286389, lng: 36.817223 }; // Default to Nairobi center
  };

  return (
    <LoadScript googleMapsApiKey={API_KEY}>
      <GoogleMap
        mapContainerStyle={containerStyle}
        center={center}
        zoom={12}
      >
        {/* Display traffic data markers */}
        {trafficData.map((event, index) => (
          <Marker
            key={index}
            position={event.location}
            onClick={() => setSelectedMarker(event)}
          />
        ))}

        {/* Display information window for selected marker */}
        {selectedMarker && (
          <InfoWindow
            position={selectedMarker.location}
            onCloseClick={() => setSelectedMarker(null)}
          >
            <div>
              <h2>{selectedMarker.road_name}</h2>
              <p>Predicted Vehicle Count: {selectedMarker.Predicted}</p>
              <p>Event Type: {getEventType(selectedMarker.Predicted)}</p>
              <p>Location: {selectedMarker.location.lat}, {selectedMarker.location.lon}</p>
              <p>Timestamp: {formatTimestamp(selectedMarker.timestamp)}</p>
            </div>
          </InfoWindow>
        )}

        {/* Display optimized route if available */}
        {routeRequested && directionsResponse && (
          <DirectionsRenderer
            directions={directionsResponse}
            options={{
              suppressMarkers: true,
            }}
          />
        )}
      </GoogleMap>

      {/* Input for origin and destination */}
      <div style={{ position: "absolute", top: "10px", left: "10px", backgroundColor: "white", padding: "10px" }}>
        <h3>Enter Route</h3>
        <input
          type="text"
          placeholder="Origin (e.g., Thika Road)"
          value={origin}
          onChange={(e) => setOrigin(e.target.value)}
        />
        <br />
        <input
          type="text"
          placeholder="Destination (e.g., Moi Avenue)"
          value={destination}
          onChange={(e) => setDestination(e.target.value)}
        />
        <br />
        <button onClick={handleRouteRequest}>Show Optimized Route</button>
      </div>

      {/* Button to show alternative transportation options */}
      <div style={{ position: "absolute", top: "120px", left: "10px", backgroundColor: "white", padding: "10px" }}>
        <button
          onClick={() => setHighlightAlternative(!highlightAlternative)}
          style={{
            backgroundColor: highlightAlternative ? "green" : "gray",
            color: "white",
            padding: "10px",
          }}
        >
          {highlightAlternative ? "Hide Alternative Transportation" : "Show Alternative Transportation"}
        </button>
      </div>
    </LoadScript>
  );
}

export default App;
