import React from 'react';
import { GoogleMap, LoadScript, Marker, DirectionsRenderer } from '@react-google-maps/api';

const trafficData = [
  {
    location: { lat: -1.286352078, lng: 36.8227607206 },
    vehicleCount: 86.79,
    eventType: 'ðŸš— Congested',
    timestamp: '18/03/2025, 09:00:00'
  },
  {
    location: { lat: -1.2921, lng: 36.8219 },
    vehicleCount: 43.25,
    eventType: 'âœ… Clear',
    timestamp: '18/03/2025, 09:05:00'
  },
  {
    location: { lat: -1.2800, lng: 36.8150 },
    vehicleCount: 95.12,
    eventType: 'ðŸš— Congested',
    timestamp: '18/03/2025, 09:10:00'
  }
];

const containerStyle = {
  width: '100%',
  height: '600px',
  borderRadius: '10px',
  marginTop: '20px'
};

const center = {
  lat: -1.286389, lng: 36.817223 // Nairobi center
};

function App() {
  const [directions, setDirections] = React.useState(null);

  React.useEffect(() => {
    const directionsService = new window.google.maps.DirectionsService();
    directionsService.route(
      {
        origin: trafficData[0].location,
        destination: trafficData[2].location,
        waypoints: [{ location: trafficData[1].location }],
        travelMode: window.google.maps.TravelMode.DRIVING,
        optimizeWaypoints: true
      },
      (result, status) => {
        if (status === 'OK') {
          setDirections(result);
        } else {
          console.error('Directions request failed due to ' + status);
        }
      }
    );
  }, []);

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1 style={{ color: '#2c3e50' }}>ðŸš¦ Traffic Prediction for Thika Road</h1>
      {trafficData.map((data, index) => (
        <div key={index} style={{ marginBottom: '10px', backgroundColor: '#ecf0f1', padding: '10px', borderRadius: '8px' }}>
          <p><strong>Predicted Vehicle Count:</strong> {data.vehicleCount.toFixed(2)}</p>
          <p><strong>Event Type:</strong> {data.eventType}</p>
          <p><strong>Location:</strong> {data.location.lat}, {data.location.lng}</p>
          <p><strong>Timestamp:</strong> {data.timestamp}</p>
        </div>
      ))}

      <LoadScript googleMapsApiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY}>
        <GoogleMap mapContainerStyle={containerStyle} center={center} zoom={13}>
          {trafficData.map((data, index) => (
            <Marker key={index} position={data.location} label={data.eventType === 'ðŸš— Congested' ? 'C' : 'C'} />
          ))}
          {directions && <DirectionsRenderer directions={directions} />}
        </GoogleMap>
      </LoadScript>
    </div>
  );
}

export default App;
