import React, { useState, useEffect, useRef } from 'react';
import { GoogleMap, LoadScript, Marker, DirectionsService, DirectionsRenderer } from '@react-google-maps/api';

const trafficData = [
  {
    Actual: 80,
    Predicted: 86.79,
    timestamp: 1742284800000,
    road_name: 'Thika Road',
    location: { lat: -1.286352078, lon: 36.8227607206 },
  },
  {
    Actual: 35,
    Predicted: 56.44,
    timestamp: 1742288400000,
    road_name: 'Thika Road',
    location: { lat: -1.301295454, lon: 36.8240535191 },
  },
  {
    Actual: 67,
    Predicted: 61.01,
    timestamp: 1742292000000,
    road_name: 'Moi Avenue',
    location: { lat: -1.2849540289, lon: 36.8287996864 },
  },
];

function getEventType(predicted) {
  if (predicted > 70) return 'ðŸš— Congested';
  if (predicted > 40) return 'ðŸš™ Moderate';
  return 'âœ… Clear';
}

function App() {
  const [selectedRoad, setSelectedRoad] = useState(null);
  const [directions, setDirections] = useState(null);
  const [routeText, setRouteText] = useState('');
  const [origin, setOrigin] = useState('');
  const [destination, setDestination] = useState('');
  const [isLoaded, setIsLoaded] = useState(false);
  const mapRef = useRef(null);
  const [mapCenter, setMapCenter] = useState({ lat: -1.286352078, lng: 36.8227607206 });

  // Simulate real-time traffic data updates
  useEffect(() => {
    const interval = setInterval(() => {
      const randomRoadIndex = Math.floor(Math.random() * trafficData.length);
      const updatedTraffic = [...trafficData];
      updatedTraffic[randomRoadIndex].Predicted = Math.random() * 100;
      setSelectedRoad(updatedTraffic[randomRoadIndex]);
    }, 10000);

    return () => clearInterval(interval);
  }, []);

  const handleCalculateRoute = () => {
    if (!origin || !destination) {
      alert('Please provide both origin and destination');
      return;
    }

    // Log origin and destination
    console.log('Calculating route from', origin, 'to', destination);

    const directionsService = new window.google.maps.DirectionsService();

    const DirectionsServiceRequest = {
      origin,
      destination,
      travelMode: 'DRIVING',
    };

    // Log DirectionsRequest to verify the input values
    console.log('Directions Service Request:', DirectionsServiceRequest);

    directionsService.route(DirectionsServiceRequest, (result, status) => {
      if (status === 'OK') {
        console.log('Directions Result:', result);  // Log the result
        setDirections(result);

        // Extract the steps of the route and generate a text description
        const routeDescription = result.routes[0].legs[0].steps
          .map((step, index) => `${index + 1}. ${step.instructions}`)
          .join('\n');
        setRouteText(routeDescription);
      } else {
        console.log('Error fetching directions:', status);
      }
    });
  };

  const onMapLoad = (map) => {
    mapRef.current = map;
    setIsLoaded(true);
  };

  return (
    <div style={{ fontFamily: 'Arial', padding: '20px' }}>
      <h1>Nairobi Smart Traffic Monitoring</h1>
      <h2>Select a Road:</h2>
      {trafficData.map((data, index) => (
        <button
          key={index}
          style={{
            margin: '5px',
            padding: '10px',
            borderRadius: '8px',
            backgroundColor: '#007BFF',
            color: 'white',
            border: 'none',
            cursor: 'pointer',
          }}
          onClick={() => setSelectedRoad(data)}
        >
          {data.road_name} - {new Date(data.timestamp).toLocaleString()}
        </button>
      ))}

      {selectedRoad && (
        <div style={{ marginTop: '20px', border: '1px solid #ccc', padding: '15px', borderRadius: '8px' }}>
          <h3>Traffic Prediction for {selectedRoad.road_name}</h3>
          <p><strong>Predicted Vehicle Count:</strong> {selectedRoad.Predicted}</p>
          <p><strong>Event Type:</strong> {getEventType(selectedRoad.Predicted)}</p>
          <p><strong>Location:</strong> {selectedRoad.location.lat}, {selectedRoad.location.lon}</p>
          <p><strong>Timestamp:</strong> {new Date(selectedRoad.timestamp).toLocaleString()}</p>
        </div>
      )}

      {/* User input for route optimization */}
      <h2>Enter Route for Optimization</h2>
      <input
        type="text"
        placeholder="Origin (e.g., Thika Road)"
        value={origin}
        onChange={(e) => setOrigin(e.target.value)}
      />
      <input
        type="text"
        placeholder="Destination (e.g., Moi Avenue)"
        value={destination}
        onChange={(e) => setDestination(e.target.value)}
      />
      <button onClick={handleCalculateRoute} style={{ padding: '10px', backgroundColor: '#28a745', color: 'white' }}>
        Get Optimized Route
      </button>

      {/* Display the optimized route text */}
      {routeText && (
        <div style={{ marginTop: '20px', border: '1px solid #ccc', padding: '15px', borderRadius: '8px' }}>
          <h3>Optimized Route:</h3>
          <pre>{routeText}</pre>
        </div>
      )}

      {/* Google Map display */}
      <LoadScript googleMapsApiKey="AIzaSyALhBSMGH2u_XWOrRO71q004IB8PYuQKW8">
        <GoogleMap
          mapContainerStyle={{ height: '400px', width: '100%' }}
          center={mapCenter}
          zoom={12}
          onLoad={onMapLoad}
        >
          {isLoaded && selectedRoad && (
            <Marker
              position={{ lat: selectedRoad.location.lat, lng: selectedRoad.location.lon }}
            />
          )}

          {/* Directions Renderer */}
          {directions && <DirectionsRenderer directions={directions} />}
        </GoogleMap>
      </LoadScript>
    </div>
  );
}

export default App;
